package com.enation.app.shop.component.member.plugin.footprint;import com.enation.app.base.core.model.Member;import com.enation.app.shop.core.member.plugin.IMemberLoginEvent;import com.enation.app.shop.core.member.service.IFootprintManager;import com.enation.framework.context.webcontext.ThreadContextHolder;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;/** * @author liuyulei * @version 1.0 * @Description: ${todo} * @date 2018-11-07 3:01 PM * @since v6.5 */@Componentpublic class FootPrintRememberPlugin implements IMemberLoginEvent {    @Autowired    private IFootprintManager footprintManager;    @Override    public void onLogin(Member member, Long upLogintime) {        String sessionid = ThreadContextHolder.getHttpRequest().getSession().getId();        this.footprintManager.update(sessionid,member.getMember_id());    }}